INSERT INTO `restart_email_template` VALUES ('2023-05-11 18:52:42.640334', '2023-05-11 18:52:42.640334', 1, 'blogger-default', '<div style=\"width:550px;height:auto;border-radius:5px;margin:0 auto;box-shadow:0px 0px 20px #888888;position:relative\">\n    <div style=\"background-image:url(//test.inis.cn/api/file/random);width:550px;height:250px;background-size:cover;background-repeat:no-repeat;border-radius:5px 5px 0px 0px\"></div>\n    <div style=\"background-color:white;line-height:180%;padding:0 15px 12px;width:520px;margin:10px auto;color:#555555;font-size:12px;margin-bottom:0px\">\n        <h2 style=\"font-size:14px;font-weight:normal;padding:13px 0 10px 8px\">您的<a style=\"text-decoration:none;color:#ff7272\">《{article}》</a>有了新的评论：</h2>\n        <div style=\"padding:0 12px 0 12px;margin-top:18px\">\n            <p>\n                <strong>{nickname}</strong>\n                &nbsp;给您的评论：\n            </p>\n            <p style=\"background-color:#f5f5f5;border:0px solid #DDD;border-radius:5px;padding:10px 15px;margin:18px 0\">{text}</p>\n            <p>详细信息：</p>\n            <p style=\"background-color:#f5f5f5;border:0px solid #DDD;border-radius:5px;padding:10px 15px;margin:18px 0\">\n                IP：{ip}\n                <br/>\n                邮箱：\n                <a href=mailto:{email}style=\"text-decoration:none;color:#ff7272\">{email}</a>\n            </p>\n        </div>\n    </div>\n    <a href={admin_url}target=_blank style=\"text-decoration:none;color:rgb(255,255,255);width:40%;text-align:center;background-color:rgb(255,114,114);height:40px;line-height:40px;box-shadow:3px 3px 3px rgba(0,0,0,0.3);display:block;margin:auto\">查看回复的完整內容</a>\n    <div style=\"color:#8c8c8c;font-size:10px;width:100%;text-align:center;padding-bottom:1px\">\n        <p>©2019-2021 Copyright{site}</p>\n    </div>\n</div>');
INSERT INTO `restart_email_template` VALUES ('2023-05-11 18:53:04.228988', '2023-05-11 18:53:04.228988', 2, 'user-default', '<div style=\"width:550px;height:auto;border-radius:5px;margin:0 auto;box-shadow:0px 0px 20px #888888;position:relative;padding-bottom:5px\">\n    <div style=\"background-image:url(//test.inis.cn/api/file/random);width:550px;height:300px;background-size:cover;background-repeat:no-repeat;border-radius:5px 5px 0px 0px\"></div>\n    <div style=\"width:200px;height:40px;background-color:rgb(255,114,114);margin-top:-20px;margin-left:20px;box-shadow:3px 3px 3px rgba(0,0,0,0.3);color:rgb(255,255,255);text-align:center;line-height:40px\">亲爱的{nickname}</div>\n    <div style=\"background-color:white;line-height:180%;padding:0 15px 12px;width:520px;margin:30px auto;color:#555555;font-size:12px;margin-bottom:0px\">\n        <h2 style=\"font-size:14px;font-weight:normal;padding:13px 0 10px 8px\">您在\n            <a href={site_url}target=_blank style=\"text-decoration:none;color:#ff7272\">《{article}》</a>\n            的评论有了新的回复：\n        </h2>\n        <div style=\"padding:0 12px 0 12px;margin-top:18px\">\n            <p>您的评论：</p>\n            <p style=\"background-color:#f5f5f5;border:0px solid #DDD;border-radius:5px;padding:10px 15px;margin:18px 0\">{text}</p>\n            <p>\n                <strong>{author}</strong>\n                &nbsp;给您的回复：\n            </p>\n            <p style=\"background-color:#f5f5f5;border:0px solid #DDD;border-radius:5px;padding:10px 15px;margin:18px 0\">{content}</p>\n        </div>\n    </div>\n    <div style=\"word-wrap:break-word;margin-top:-30px;color:#8c8c8c;font-size:10px;width:100%;text-align:center;\">\n        <p style=\"padding:20px\">萤火虫消失之后，那光的轨迹仍久久地印在我的脑际。那微弱浅淡的光点，仿佛迷失方向的魂灵，在漆黑厚重的夜幕中彷徨。——《挪威的森林》村上春树</p>\n    </div>\n    <a href={site_url}target=_blank style=\"text-decoration:none; color:#FFF;width: 40%;text-align: center;background-color:#ff7272;height: 40px;line-height: 35px;box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.30);margin: -10px auto;display: block;\">查看回复的完整內容</a>\n    <div style=\"color:#8c8c8c;font-size:10px;width:100%;text-align:center;\">\n        <p style=\"margin-top:30px\">本邮件为系统自动发送，请勿直接回复~</p>\n    </div>\n    <div style=\"color:#8c8c8c;font-size:10px;width:100%;text-align:center;\">\n        <p>©2019-2021 Copyright{site}</p>\n    </div>\n</div>');
